            for (var i = 0; i < remainingPieces; i++) {
              const currentReceiptNumber = this.model.RcptNmbr[this.rcptNoCount].RcptNmbr.toString();
              totalwt += parseFloat(wght);
              const row = {
                wght: parseFloat(wght),
