  //Generate receipt numbers
  generateReceiptNumbers(qnty) {
    // if(this.pTypeSelected=='')
    // {
    //   Swal.fire({
    //     icon: 'info',
    //     title: 'Information',
    //     text: "Please Select Product Type First", 
    //   });
    //   return;
    // }
    if (!qnty) {
      Swal.fire({
        icon: 'info',
        title: 'Information',
        text: "Please Enter No. of pieces"
      });
      return;
    }
    this.rcptNoCount = 0;
    this.rcptCountDim = 0;
    this.recordToAdd = 0;
    this.recordToAddWght = 0;
    let lastRcptNmbr = "null";
    // if(this.receiptNumberArray.length >0)
    // {
    //    lastRcptNmbr = this.receiptNumberArray[this.receiptNumberArray.length-1].RcptNmbr;
    // }
    Swal.showLoading();
    this.apiService.GenerateReceiptNumbers(qnty, lastRcptNmbr).subscribe(response => {
      console.log(response);
      this.model.RcptNmbr = [];
      for (let i = 0; i < response.length; i++) {
        let receiptItem = {
          RcptNmbr: response[i].toString()
        };
        this.model.RcptNmbr?.push(receiptItem);
        this.receiptNumberArray.push(receiptItem);
      }
    }, () => {
      console.log("error");
    }, () => {
      console.log("success");
      Swal.close();
      this.imagesArray = [];
    });
  }
  //check for Duplicate Shipment Number 
  checkDuplicateShipment(shpNmbr) {
    this.apiService.checkDuplicateShipmentNumber(shpNmbr).subscribe(response => {
      console.log(response);
      // this.toast();
    }, error => {
      Swal.fire({
        icon: 'info',
        title: 'Information',
        text: "This Shipment Number already exists"
      });
      return;
      console.log(error);
    });
  }
