  // }
  Swal.showLoading();
  this.apiService.GenerateReceiptNumbers(qnty,lastRcptNmbr).subscribe(
 (response:String[])=>{
console.log(response);
this.model.RcptNmbr=[];
for (let i = 0; i < response.length; i++) {
