//check for Duplicate Shipment Number 
public checkDuplicateShipment(shpNmbr:string)
{
  this.apiService.checkDuplicateShipmentNumber(shpNmbr).subscribe(
    (response: string)=>{
      console.log(response);
     // this.toast();
    },
    (error)=>{
      Swal.fire({
        icon: 'info',
        title: 'Information',
