import { Component } from '@angular/core';
import { ApiService } from '../api.service';
import { Shipment } from '../Models/shipment';
import { DataSource } from '@angular/cdk/collections';
import { MatTableDataSource } from '@angular/material/table';


@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent {
  shipmentData: Shipment[];
  constructor(private apiService: ApiService){
    this.shipmentData=[];
  }
  //displayedColumns: any[] = ['position', 'name', 'weight', 'symbol'];

  displayedColumns: string[] = ['ShptNmbr', 'Name', 'Locn','Qnty'];
  dataSource:any;

  ngOnInit(): void {
    this.getShipments();
  }

  getShipments(){
    this.apiService.getAllShipments().subscribe(
      (response:any)=>{
        this.shipmentData=response;
        this.dataSource=new MatTableDataSource(this.shipmentData);
        console.log("list=" ,this.dataSource);
        console.log(this.shipmentData);
      },
      (error)=>{
        console.log("Api call failed",error);
      }
    );
  }
}
