import { Component } from '@angular/core';
import { ApiService } from '../api.service';


@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent {
shipmentData:any[]=[];
  constructor(private apiService: ApiService){}
  displayedColumns: string[] = ['Shipment No.', 'Name', 'Quantity', 'Location'];

  ngOnInit(): void {
    this.getShipments();
  }

  getShipments(){
    this.apiService.getAllShipments().subscribe(
      (response:any)=>{
        this.shipmentData=response;
      },
      (error)=>{
        console.log("Api call failed",error);
      }
    );
  }
}
<div class="table-container">
    <table mat-table  class="mat-elevation-z8" [dataSource]="shipmentData">
        <ng-container matColumnDef="shptNmbr">
            <th mat-header-cell *matHeaderCellDef> Shipment No. </th>
            <td mat-cell *matCellDef="let element"> {{element.shptNmbr}} </td>
        </ng-container>
        <ng-container matColumnDef="name">
            <th mat-header-cell *matHeaderCellDef> Name </th>
            <td mat-cell *matCellDef="let element"> {{element.name}} </td>
        </ng-container>
        <ng-container matColumnDef="Quantity">
            <th mat-header-cell *matHeaderCellDef> Quantity </th>
            <td mat-cell *matCellDef="let element"> {{element.Qnty}} </td>
        </ng-container>
        <ng-container matColumnDef="Location">
            <th mat-header-cell *matHeaderCellDef> Location </th>
            <td mat-cell *matCellDef="let element"> {{element.Locn}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
</div>
