import { Component } from '@angular/core';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  constructor(private router: Router) {}
  username: string = '';
  password: string = '';

  submitForm() {
    console.log('Username:', this.username);
    console.log('Password:', this.password);
    this.router.navigate(['/welcome']);
    // if(this.username=='pwswarehouse.user' && this.password=="Priority1"){
    //   this.router.navigate(['/welcome']);
    // }
    // else{
    //  this.errorToast();
    // }


  }

  //error toast
  async errorToast(){
    const Toast = Swal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 3000,
      timerProgressBar: true,
      didOpen: (toast) => {
        toast.addEventListener('mouseenter', Swal.stopTimer)
        toast.addEventListener('mouseleave', Swal.resumeTimer)
      }
    })
    
    Toast.fire({
      icon: 'error',
      title: 'Incorrect User name or Password'
    });
  }
}

<!-- Background & animion & navbar & title -->
<!-- <div class="container-fluid">
  
</div> -->

<div class="page-container">
      <!-- Background animtion-->
  <!-- <div class="background"> -->
    <div class="cube"></div>
    <div class="cube"></div>
    <div class="cube"></div>
    <div class="cube"></div>
   <div class="cube"></div>
 <!-- </div> -->
    <div class="login-container">
      <img src="\assets\logo-img.png" alt="Logo" class="logo">
     
      <h2 class="login-txt">Login</h2>
      <form (ngSubmit)="submitForm()">
        <div>
          <label class="font-color" for="username">Username</label>
          <input type="text" id="username" [(ngModel)]="username" name="username" required>
        </div>
        <div>
          <label class="font-color" for="password">Password</label>
          <input type="password" id="password" [(ngModel)]="password" name="password" required>
        </div>
        <button  type="submit">Login</button>
      </form>
    </div>
  </div>
