import { Component, ElementRef, ViewChild } from '@angular/core';
import { trigger, state, style, transition, animate } from '@angular/animations';
import { ApiService } from '../api.service';
import { Shipment } from '../Models/shipment';
import { FormGroup } from '@angular/forms';
import { FormBuilder } from '@angular/forms';
import { MatSelectModule } from '@angular/material/select';
import { QrcodeService } from '../qrcode.service';
import { Route, Router } from '@angular/router';
import { MessageService } from '../messsage.service';
import { NotificationsService } from 'angular2-notifications';
import Swal from 'sweetalert2';
import { Subscription, finalize } from 'rxjs';
import { HttpClient, HttpEventType } from '@angular/common/http';
@Component({
  selector: 'app-cargo-shipment',
  templateUrl: './cargo-shipment.component.html',
  styleUrls: ['./cargo-shipment.component.css'],
  animations: [
    trigger('fadeIn', [
      state('void', style({ opacity: 0 })),
      transition(':enter', [animate('300ms ease-in', style({ opacity: 1 }))]),
    ])
  ]
})
export class CargoShipmentComponent {



  requiredFileType:string='';

  fileName = '';
  uploadProgress:number=0;
  uploadSub: Subscription;

  @ViewChild('fileInput') fileInput!: ElementRef;
  model: Shipment = new Shipment();
  form: FormGroup;
  selectedFile: File | undefined;
   imageBytes: ArrayBuffer | string | null;
  uploadedImages: File[] = [];
  qrCodeData: string[] = [];
  errorMessage!: string;
  emptyFields: string[] = [];
  showSpinner: boolean = false;

  validationStatus = {
    clientName: true,
    shipmentNo: true,
    pieces: true,
    dimensions: true,
    weight: true,
    bayLocation: true,
    note: true,
    images: true,
    email: true,
  };
  imagesArray: string[] = [];
  selectedOption: string = '';
  constructor(private http: HttpClient,private apiService: ApiService, private fb: FormBuilder, private qrCodeService: QrcodeService, private router: Router,private _service:NotificationsService) {
    this.form = this.fb.group({
      file: [''],
    });
    this.imageBytes = null;
    this.model = new Shipment();
    this.model.Qnty = 0;
    this.imagesArray = [];
    this.uploadSub = new Subscription();


  }

  handleOptionSelection(option: string) {
    this.selectedOption = option;
  }

  onSubmit() {
    if (this.validateFormFields()) {
      this.convertArrayToJson();
      this.showSpinner = true;
      Swal.showLoading();
      this.apiService.postFormData(this.model).subscribe(
        (response: string[]) => {
          console.log('Response:', response);
          this.generateQRCodeData(response);
          //Swal.close();
          Swal.fire({
            title: 'Thank You',
            text: 'Form submitted successfully',
            icon: 'success',
            showConfirmButton: false,
            timer: 3000 
          });
          this.router.navigate(['/success']);
        },
        (error) => {
          // Handle the error here
          console.error('Error:', error);
          this.showSpinner = false; 
          Swal.close();
          Swal.fire({
            icon: 'error',
            title: 'Oops...',
            text: error, 
          });
        },
        () => {
          this.showSpinner = false; 
          //Swal.close();
      }

      );
    }
    else{
      this.displayError();
    }
  }
  convertArrayToJson() {
   // this.model.Imgs = JSON.stringify(this.imagesArray);
    this.model.Imgs=JSON.stringify(this.imageBytes);
  }
  // Define a function to validate a specific field
  validateField(fieldName: string) {
    switch (fieldName) {
      case 'clientName':
        this.validationStatus.clientName = !!this.model.Name;
        break;
      case 'shipmentNo':
        this.validationStatus.shipmentNo = !!this.model.ShptNmbr;
        break;
      case 'pieces':
        this.validationStatus.pieces = !!this.model.Qnty;
        break;
      case 'dimensions':
        this.validationStatus.dimensions = !!this.model.Dmnsn;
        break;
      case 'weight':
        this.validationStatus.weight = !!this.model.Wght;
        break;
      case 'bayLocation':
        this.validationStatus.bayLocation = !!this.model.Locn;
        break;
      case 'note':
        this.validationStatus.note = !!this.model.Note;
        break;
      case 'images':
        this.validationStatus.images = Array.isArray(this.imagesArray) && this.imagesArray.length > 0;
        break;
      case 'email':
        this.validationStatus.email = !!this.model.Rpnt;
        break;
      default:
      // Handle other fields or unknown fields here
    }
  }
  // Define a function to validate all fields
  validateFormFields() {
    this.validateField('clientName');
    this.validateField('shipmentNo');
    this.validateField('pieces');
    this.validateField('dimensions');
    this.validateField('weight');
    this.validateField('bayLocation');
    this.validateField('note');
    this.validateField('images');
    this.validateField('recipients');
    // Check if any field failed validation
    return !Object.values(this.validationStatus).some((status) => !status);
  }
  // onFileChange(event: any) {
  //   // const file = event.target.files[0];
  //   // if (file) {
  //   //   this.form.get('file')?.setValue(file);
  //   //   this.convertToBase64();
  //   //   this.imagesArray.push(file.name);
  //   // }
  //   this.selectedFile=event.target.files[0];
  //   this.convertToBase64();
  // }


  onFileChange(event:any) {
    if(this.model.ShptNmbr==''){
      Swal.fire({
        icon: 'info',
        title: 'Information',
        text: "Please add Shipment number first", 
      });
      return;
    }
    const file:File = event.target.files[0];

    if (file) {
        this.fileName = file.name;
        const formData = new FormData();
        formData.append("thumbnail", file);

        const upload$ = this.http.post("https://localhost:7196/api/Shipment/UploadImage", formData, {
            reportProgress: true,
            observe: 'events'
          //   headers: {
          //     'Content-Type': 'multipart/form-data' // Ensure the proper content type is set
          // }
        })
        .pipe(
            finalize(() => this.reset())
        );

        this.uploadSub = upload$.subscribe(event => {
          this.uploadProgress=20;
          if (event.type == HttpEventType.UploadProgress) {
            if (event.total) {
              this.uploadProgress = Math.round(100 * (event.loaded / event.total));
          }

          }
        });
        this.imagesArray.push(file.name);
        this.uploadProgress=100;
    }
}

cancelUpload() {
this.uploadSub.unsubscribe();
this.reset();
}

reset() {
this.uploadProgress = 0;
this.uploadSub = new Subscription();

}


  // convertToBase64() {
  //   const reader = new FileReader();
  //   reader.readAsDataURL(this.selectedFile as Blob);
  //   reader.onload = () => {
  //     this.imageBytes = reader.result;
  //   };
  //   reader.onerror = (error) => {
  //     console.log('Error: ', error);
  //   };
  // }

  triggerFileInput() {
    this.fileInput.nativeElement.click();
  }

  async generateQRCodeData(response:string[]): Promise<void> {
    // const warehouseReceiptNo = 'WR1000'; // Replace with your logic to generate the receipt number
     if (this.model.Qnty !== null) {
       for (let i = 1; i <= this.model.Qnty; i++) {
         const qrCodeData = `Receipt Number: ${response[i-1]}\nClient: ${this.model.Name}\nShipment No: ${this.model.ShptNmbr}`;
         try {
           const qrCodeUrl = await this.qrCodeService.generateQRCode(qrCodeData);
           this.qrCodeData.push(qrCodeUrl);
         } catch (error) {
           console.error('Error generating QR code:', error);
         }
       }
       this.apiService.qrs = this.qrCodeData;
       this.apiService.rcptNmbrs=response;
     }
   }

   ngAfterViewInit(){
    this._service.info(
      'Welcome',
      'Welcome to the Cargo',
      {
        position: ['top', 'right'],
          timeOut: 4000,
          showProgressBar: true,
          pauseOnHover: false,
          clickToClose: false,
          maxLength: 10
      }
  )
}

displayError(){
  this._service.error(
    'Error',
    'All fields are mandatory',
    {
      position: ['top', 'right'],
        timeOut: 4000,
        showProgressBar: true,
        pauseOnHover: false,
        clickToClose: false,
        maxLength: 10
    }
)
}
}
<div class="service-container">
  <mat-progress-spinner *ngIf="showSpinner" mode="indeterminate"></mat-progress-spinner>
  <div class="header-image">
    </div>
  <form class="form-container" #shipmentForm="ngForm" [@fadeIn]>
      <div class="left-column">
    <mat-form-field>
      <mat-label>Client</mat-label>
      <input matInput type="text" name="client" [(ngModel)]="model.Name" (ngModelChange)="validateField('clientName')" required>
      <div  class="error-message" *ngIf="!validationStatus.clientName ">
       Please Enter Client Name
      </div>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Shipment No</mat-label>
      <input matInput type="text" name="shipmentNo" [(ngModel)]="model.ShptNmbr" (ngModelChange)="validateField('shipmentNo')" required>
      <div  class="error-message" *ngIf="!validationStatus.shipmentNo ">
        Please Enter Shipment Number
       </div>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Pieces</mat-label>
      <input matInput type="number" name="pieces" [(ngModel)]="model.Qnty" (ngModelChange)="validateField('pieces')" required>
      <div  class="error-message" *ngIf="!validationStatus.pieces ">
        Please Enter Number of Pieces
       </div>
    </mat-form-field> 
    <mat-radio-group aria-label="Select an option"  [(ngModel)]="dimensionSelectedOption">
      <mat-radio-button value="cm" (change)="dimensionOptionSelected('cm')" [disabled]="dimensionSelectedOption !== ''">Centimeter</mat-radio-button>
      <mat-radio-button value="in" (change)="dimensionOptionSelected('in')" [disabled]="dimensionSelectedOption !== ''">Inches</mat-radio-button>
    </mat-radio-group>
    <mat-form-field>
      <mat-label>Dimensions (cm/in)</mat-label>
      <input matInput type="text" name="dimensions" [(ngModel)]="model.Dmnsn" (ngModelChange)="validateField('dimensions')" required>
      <div  class="error-message" *ngIf="!validationStatus.dimensions ">
        Please Enter Dimensions
       </div>
    </mat-form-field>
    <mat-radio-group aria-label="Select an option"  [(ngModel)]="weightSelectedOption">
      <mat-radio-button value="kg" (change)="weightOptionSelected('kg')" [disabled]="weightSelectedOption !== ''">Kilogram</mat-radio-button>
      <mat-radio-button value="lb" (change)="weightOptionSelected('lb')" [disabled]="weightSelectedOption !== ''">Pound</mat-radio-button>
    </mat-radio-group>
    <mat-form-field>
      <mat-label>Weight (lb/kg)</mat-label>
      <input matInput type="text" name="weight" [(ngModel)]="model.Wght" (ngModelChange)="validateField('weight')" required>
      <div  class="error-message" *ngIf="!validationStatus.weight ">
        Please Enter Weight
       </div>
    </mat-form-field>
  </div>
  <div class="right-column">
      <mat-form-field>
          <mat-label>Bay Location</mat-label>
          <input matInput type="text" name="bay location" [(ngModel)]="model.Locn" (ngModelChange)="validateField('bayLocation')" required>
          <div  class="error-message" *ngIf="!validationStatus.bayLocation ">
            Please Enter Bay Location
           </div>
        </mat-form-field>
        <mat-form-field>
          <mat-label>Note</mat-label>
          <input matInput type="text" name="Note" [(ngModel)]="model.Note" (ngModelChange)="validateField('note')" required>
          <div  class="error-message" *ngIf="!validationStatus.note ">
            Please Enter Free fall Note
           </div>
        </mat-form-field>
        <mat-form-field>
          <mat-label>Select Image</mat-label>
          <input
            type="file"
            #fileInput
            (change)="onFileChange($event)"
            accept="image/*"
            style="display: none;"
            multiple
          />
          <button mat-icon-button (click)="fileInput.click()">
            <mat-icon>attach_file</mat-icon>
          </button>
          <input matInput [value]="form.get('file')?.value ? form.get('file')?.value.name : ''" readonly (ngModelChange)="validateField('images')" />  
          <div class="error-message" *ngIf="model.Qnty && model.Qnty > 0 && uploadedImages.length < model.Qnty">
           <div *ngIf="model.Qnty - imagesArray.length !=0">Please add {{ model.Qnty - imagesArray.length }} more image(s).</div>   
            </div>
            <div  class="error-message" *ngIf="!validationStatus.images ">
              Please Select required Number of Images
             </div>

             <div class="progress">

              <mat-progress-bar class="progress-bar" mode="determinate"
                                [value]="uploadProgress" *ngIf="uploadProgress">

              </mat-progress-bar>

              <mat-icon class="cancel-upload" (click)="cancelUpload()" 
                        *ngIf="uploadProgress">delete_forever</mat-icon>

            </div>
        </mat-form-field>
        <mat-form-field>
          <mat-label>Select Recipients</mat-label>
          <mat-select name="recipients" [(ngModel)]="model.Rpnt"  (ngModelChange)="validateField('email')" required>
            <mat-option value="Accounting@priorityworldwide.com">Accounting@priorityworldwide.com</mat-option>
            <mat-option value="Operations@priorityworldwide.com">Operations@priorityworldwide.com</mat-option>
            <mat-option value="Exports@priorityworldwide.com">Exports@priorityworldwide.com</mat-option>
            <mat-option value="Imports@priorityworldwide.com">Imports@priorityworldwide.com</mat-option>
            <mat-option value="HR@priorityworldwide.com">HR@priorityworldwide.com</mat-option>

          </mat-select>
          <div class="error-message" *ngIf="!validationStatus.email">
            Please Select Recipients
          </div>
        </mat-form-field>
        <mat-form-field>
          <input type="text" matInput placeholder="Enter Custom recipients" name="additionalRecipients" [(ngModel)]="model.CstmRpnt">
        </mat-form-field>
    </div>
  </form>
  <div class="center-button">
      <button mat-raised-button color="primary"  type="submit" (click)="onSubmit()">Save<mat-icon>save</mat-icon></button>
  </div>
</div>
